export function navigateTo(t){window.history.pushState({},"",t),router()}const routes={"/":"home","/projetos":"projetos","/cadastro":"cadastro"};export function router(){const t=window.location.pathname,e=routes[t]||"home";window.dispatchEvent(new CustomEvent("routechange",{detail:e})),updateActiveLinks(t)}function updateActiveLinks(t){document.querySelectorAll(".menu__item, .navdropdown a").forEach(e=>e.classList.toggle("active",e.getAttribute("href")===t))}